package ExercicioLoop;

public class EstruturaComContinue {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for (int cont =0;cont<=10;cont++) {
			
			if(cont == 3 |cont == 5 | cont == 7 ) {
				System.out.println("Oba encontramos o numero "+cont);
				System.out.println("");
				continue;
			}
			
			
		}
		
		
	}

}
